function mensaje_error(msg){   
	alertify.error("<div class='icon_msg'><img src=\"/bundles/hnrsircim/img/error.png\"></div><div class=\"msg\">"+msg+"</div>");
}

function mensaje_exito(msg){   
 	alertify.success("<div class='icon_msg'><img src=\"/bundles/hnrsircim/img/success.png\"></div><div class=\"msg\">"+msg+"</div>");
}    

function mensaje_info(msg){   
    alertify.log("<div class='icon_msg'><img src=\"/bundles/hnrsircim/img/info.png\"></div><div class=\"msg\">"+msg+"</div>");
}

